/* General Styles */
@import url('https://fonts.googleapis.com/css2?family=Comfortaa:wght@300;400;600;700&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --dark-color: #000;
    --primary-color: #6bf0ff;
    --secondary-color: #a7ffee;
    --accent-color: #54b8aa;
    --light-color: #fff;
    --text-color: #333;
    --bg-color: #f8f9fa;
}

body {
    font-family: 'Comfortaa', cursive;
    color: var(--text-color);
    background: var(--bg-color);
    transition: all 0.3s ease;
}

/* Hide main app initially */
.main-app.d-none {
    display: none !important;
}

/* Screen management */
.screen {
    display: none;
}

.screen.active {
    display: block;
}

/* Animations */
@keyframes fade-in {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in {
    animation: fade-in 1.5s ease-out forwards;
}

@keyframes heart-beat {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.1);
    }
}

.heart-beat {
    animation: heart-beat 1.5s infinite;
}

@keyframes collected-heart {
    0% {
        transform: scale(1);
        opacity: 1;
    }
    100% {
        transform: scale(1.5) translateY(-50px);
        opacity: 0;
    }
}

.collected-heart {
    animation: collected-heart 0.5s ease-out forwards;
}

@keyframes float {
    0%, 100% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(-10px);
    }
}

.floating {
    animation: float 3s ease-in-out infinite;
}

/* Flower Animation Styles */
.flower-animation-body {
    display: flex;
    align-items: flex-end;
    justify-content: center;
    min-height: 100vh;
    background-color: var(--dark-color);
    overflow: hidden;
    perspective: 1000px;
}

.flower-intro-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 9999;
    background: var(--dark-color);
}

.night {
    position: fixed;
    left: 50%;
    top: 0;
    transform: translateX(-50%);
    width: 100%;
    height: 100%;
    filter: blur(0.1vmin);
    background-image: 
        radial-gradient(ellipse at top, transparent 0%, var(--dark-color)),
        radial-gradient(ellipse at bottom, var(--dark-color), rgba(145, 233, 255, 0.2)),
        repeating-linear-gradient(220deg, black 0px, black 19px, transparent 19px, transparent 22px),
        repeating-linear-gradient(189deg, black 0px, black 19px, transparent 19px, transparent 22px),
        repeating-linear-gradient(148deg, black 0px, black 19px, transparent 19px, transparent 22px),
        linear-gradient(90deg, #2d1b69, #11072c, #2d1b69);
}

.flowers {
    position: relative;
    transform: scale(0.9);
}

.flower {
    position: absolute;
    bottom: 10vmin;
    transform-origin: bottom center;
    z-index: 10;
    --fl-speed: 0.8s;
}

.flower--1 {
    animation: moving-flower-1 4s linear infinite;
}

.flower--1 .flower__line {
    height: 70vmin;
    animation-delay: 0.3s;
}

.flower--1 .flower__line__leaf--1 {
    animation: blooming-leaf-right var(--fl-speed) 1.6s backwards;
}

.flower--1 .flower__line__leaf--2 {
    animation: blooming-leaf-right var(--fl-speed) 1.4s backwards;
}

.flower--1 .flower__line__leaf--3 {
    animation: blooming-leaf-left var(--fl-speed) 1.2s backwards;
}

.flower--1 .flower__line__leaf--4 {
    animation: blooming-leaf-left var(--fl-speed) 1s backwards;
}

.flower--1 .flower__line__leaf--5 {
    animation: blooming-leaf-right var(--fl-speed) 1.8s backwards;
}

.flower--1 .flower__line__leaf--6 {
    animation: blooming-leaf-left var(--fl-speed) 2s backwards;
}

.flower--2 {
    left: 50%;
    transform: rotate(20deg);
    animation: moving-flower-2 4s linear infinite;
}

.flower--2 .flower__line {
    height: 60vmin;
    animation-delay: 0.6s;
}

.flower--2 .flower__line__leaf--1 {
    animation: blooming-leaf-right var(--fl-speed) 1.9s backwards;
}

.flower--2 .flower__line__leaf--2 {
    animation: blooming-leaf-right var(--fl-speed) 1.7s backwards;
}

.flower--2 .flower__line__leaf--3 {
    animation: blooming-leaf-left var(--fl-speed) 1.5s backwards;
}

.flower--2 .flower__line__leaf--4 {
    animation: blooming-leaf-left var(--fl-speed) 1.3s backwards;
}

.flower--3 {
    left: 50%;
    transform: rotate(-15deg);
    animation: moving-flower-3 4s linear infinite;
}

.flower--3 .flower__line {
    animation-delay: 0.9s;
}

.flower--3 .flower__line__leaf--1 {
    animation: blooming-leaf-right var(--fl-speed) 2.5s backwards;
}

.flower--3 .flower__line__leaf--2 {
    animation: blooming-leaf-right var(--fl-speed) 2.3s backwards;
}

.flower--3 .flower__line__leaf--3 {
    animation: blooming-leaf-left var(--fl-speed) 2.1s backwards;
}

.flower--3 .flower__line__leaf--4 {
    animation: blooming-leaf-left var(--fl-speed) 1.9s backwards;
}

.flower__leafs {
    position: relative;
    animation: blooming-flower 2s backwards;
}

.flower__leafs--1 {
    animation-delay: 1.1s;
}

.flower__leafs--2 {
    animation-delay: 1.4s;
}

.flower__leafs--3 {
    animation-delay: 1.7s;
}

.flower__leafs::after {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    transform: translate(-50%, -100%);
    width: 8vmin;
    height: 8vmin;
    background-color: #6bf0ff;
    filter: blur(10vmin);
}

.flower__leaf {
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 8vmin;
    height: 11vmin;
    border-radius: 51% 49% 47% 53% / 44% 45% 55% 69%;
    background-color: #a7ffee;
    background-image: linear-gradient(to top, #54b8aa, #a7ffee);
    transform-origin: bottom center;
    opacity: 0.9;
    box-shadow: inset 0 0 2vmin rgba(255, 255, 255, 0.5);
}

.flower__leaf--1 {
    transform: translate(-10%, 1%) rotateY(40deg) rotateX(-50deg);
}

.flower__leaf--2 {
    transform: translate(-50%, -4%) rotateX(40deg);
}

.flower__leaf--3 {
    transform: translate(-90%, 0%) rotateY(45deg) rotateX(50deg);
}

.flower__leaf--4 {
    width: 8vmin;
    height: 8vmin;
    transform-origin: bottom left;
    border-radius: 4vmin 10vmin 4vmin 4vmin;
    transform: translate(0%, 18%) rotateX(70deg) rotate(-43deg);
    background-image: linear-gradient(to top, #39c6d6, #a7ffee);
    z-index: 1;
    opacity: 0.8;
}

.flower__white-circle {
    position: absolute;
    left: -3.5vmin;
    top: -3vmin;
    width: 9vmin;
    height: 4vmin;
    border-radius: 50%;
    background-color: #fff;
}

.flower__white-circle::after {
    content: "";
    position: absolute;
    left: 50%;
    top: 45%;
    transform: translate(-50%, -50%);
    width: 60%;
    height: 60%;
    border-radius: inherit;
    background-image: 
        repeating-linear-gradient(135deg, rgba(0, 0, 0, 0.03) 0px, rgba(0, 0, 0, 0.03) 1px, transparent 1px, transparent 12px),
        repeating-linear-gradient(45deg, rgba(0, 0, 0, 0.03) 0px, rgba(0, 0, 0, 0.03) 1px, transparent 1px, transparent 12px),
        linear-gradient(90deg, rgb(195, 199, 191), rgb(235, 235, 235));
}

.flower__line {
    height: 55vmin;
    width: 1.5vmin;
    background-image: 
        linear-gradient(to left, rgba(0, 0, 0, 0.2), transparent, rgba(255, 255, 255, 0.2)),
        linear-gradient(to top, transparent 10%, #14757a, #39c6d6);
    box-shadow: inset 0 0 2px rgba(0, 0, 0, 0.5);
    animation: grow-flower-tree 4s backwards;
}

.flower__line__leaf {
    --w: 7vmin;
    --h: calc(var(--w) + 2vmin);
    position: absolute;
    top: 20%;
    left: 90%;
    width: var(--w);
    height: var(--h);
    border-top-right-radius: var(--h);
    border-bottom-left-radius: var(--h);
    background-image: linear-gradient(to top, rgba(20, 117, 122, 0.4), #39c6d6);
}

.flower__line__leaf--1 {
    transform: rotate(70deg) rotateY(30deg);
}

.flower__line__leaf--2 {
    top: 45%;
    transform: rotate(70deg) rotateY(30deg);
}

.flower__line__leaf--3,
.flower__line__leaf--4,
.flower__line__leaf--6 {
    border-top-right-radius: 0;
    border-bottom-left-radius: 0;
    border-top-left-radius: var(--h);
    border-bottom-right-radius: var(--h);
    left: -460%;
    top: 12%;
    transform: rotate(-70deg) rotateY(30deg);
}

.flower__line__leaf--4 {
    top: 40%;
}

.flower__line__leaf--5 {
    top: 0;
    transform-origin: left;
    transform: rotate(70deg) rotateY(30deg) scale(0.6);
}

.flower__line__leaf--6 {
    top: -2%;
    left: -450%;
    transform-origin: right;
    transform: rotate(-70deg) rotateY(30deg) scale(0.6);
}

.flower__light {
    position: absolute;
    bottom: 0vmin;
    width: 1vmin;
    height: 1vmin;
    background-color: #fffb00;
    border-radius: 50%;
    filter: blur(0.2vmin);
    animation: light-ans 4s linear infinite backwards;
}

.flower__light:nth-child(odd) {
    background-color: #23f0ff;
}

.flower__light--1 {
    left: -2vmin;
    animation-delay: 1s;
}

.flower__light--2 {
    left: 3vmin;
    animation-delay: 0.5s;
}

.flower__light--3 {
    left: -6vmin;
    animation-delay: 0.3s;
}

.flower__light--4 {
    left: 6vmin;
    animation-delay: 0.9s;
}

.flower__light--5 {
    left: -1vmin;
    animation-delay: 1.5s;
}

.flower__light--6 {
    left: -4vmin;
    animation-delay: 3s;
}

.flower__light--7 {
    left: 3vmin;
    animation-delay: 2s;
}

.flower__light--8 {
    left: -6vmin;
    animation-delay: 3.5s;
}

.flower__grass {
    --c: #159faa;
    --line-w: 1.5vmin;
    position: absolute;
    bottom: 12vmin;
    left: -7vmin;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    z-index: 20;
    transform-origin: bottom center;
    transform: rotate(-48deg) rotateY(40deg);
}

.flower__grass--1 {
    animation: moving-grass 2s linear infinite;
}

.flower__grass--2 {
    left: 2vmin;
    bottom: 10vmin;
    transform: scale(0.5) rotate(75deg) rotateX(10deg) rotateY(-200deg);
    opacity: 0.8;
    z-index: 0;
    animation: moving-grass--2 1.5s linear infinite;
}

.flower__grass--top {
    width: 7vmin;
    height: 10vmin;
    border-top-right-radius: 100%;
    border-right: var(--line-w) solid var(--c);
    transform-origin: bottom center;
    transform: rotate(-2deg);
}

.flower__grass--bottom {
    margin-top: -2px;
    width: var(--line-w);
    height: 25vmin;
    background-image: linear-gradient(to top, transparent 30%, var(--c));
}

/* Flower Animation Keyframes */
@keyframes moving-flower-1 {
    0%, 100% {
        transform: rotate(2deg);
    }
    50% {
        transform: rotate(-2deg);
    }
}

@keyframes moving-flower-2 {
    0%, 100% {
        transform: rotate(18deg);
    }
    50% {
        transform: rotate(22deg);
    }
}

@keyframes moving-flower-3 {
    0%, 100% {
        transform: rotate(-18deg);
    }
    50% {
        transform: rotate(-12deg);
    }
}

@keyframes blooming-leaf-right {
    0% {
        transform-origin: left;
        transform: rotate(70deg) rotateY(30deg) scale(0);
    }
}

@keyframes blooming-leaf-left {
    0% {
        transform-origin: right;
        transform: rotate(-70deg) rotateY(30deg) scale(0);
    }
}

@keyframes grow-flower-tree {
    0% {
        height: 0;
    }
}

@keyframes blooming-flower {
    0% {
        transform: scale(0);
    }
}

@keyframes moving-grass {
    0%, 100% {
        transform: rotate(-48deg) rotateY(40deg);
    }
    50% {
        transform: rotate(-50deg) rotateY(40deg);
    }
}

@keyframes moving-grass--2 {
    0%, 100% {
        transform: scale(0.5) rotate(75deg) rotateX(10deg) rotateY(-200deg);
    }
    50% {
        transform: scale(0.5) rotate(73deg) rotateX(10deg) rotateY(-200deg);
    }
}

@keyframes light-ans {
    0% {
        opacity: 0;
        transform: translateY(0vmin);
    }
    25% {
        opacity: 1;
        transform: translateY(-5vmin);
    }
    50% {
        opacity: 1;
        transform: translateY(-15vmin);
    }
    75% {
        opacity: 1;
        transform: translateY(-25vmin);
    }
    100% {
        opacity: 0;
        transform: translateY(-35vmin);
    }
}

/* Custom Components */
.passcode-card {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border: none;
    border-radius: 20px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.character-card {
    border: none;
    border-radius: 15px;
    transition: all 0.3s ease;
    cursor: pointer;
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(10px);
}

.character-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.1);
}

.character-icon {
    font-size: 4rem;
    margin-bottom: 1rem;
}

.garden-navbar {
    background: rgba(255, 255, 255, 0.95) !important;
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}

.content-area {
    min-height: calc(100vh - 76px);
    padding: 2rem 0;
}

.section-card {
    background: rgba(255, 255, 255, 0.9);
    border: none;
    border-radius: 15px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
    transition: all 0.3s ease;
}

.section-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.12);
}

/* Game specific styles */
.game-board {
    display: grid;
    gap: 5px;
    margin: 20px auto;
    max-width: 400px;
}

.sudoku-board {
    grid-template-columns: repeat(9, 1fr);
}

.tictactoe-board {
    grid-template-columns: repeat(3, 1fr);
    max-width: 300px;
}

.cardmatch-board {
    grid-template-columns: repeat(4, 1fr);
    max-width: 320px;
}

.game-cell {
    aspect-ratio: 1;
    border: 2px solid #ddd;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.2rem;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    background: white;
}

.game-cell:hover {
    background: #f0f0f0;
}

.game-cell.selected {
    background: #007bff;
    color: white;
}

.heart-game-area {
    position: relative;
    height: 400px;
    border: 2px dashed #ddd;
    border-radius: 15px;
    overflow: hidden;
    background: linear-gradient(45deg, #f8f9fa, #e9ecef);
}

.heart {
    position: absolute;
    font-size: 2rem;
    cursor: pointer;
    transition: all 0.3s ease;
    user-select: none;
}

.heart:hover {
    transform: scale(1.2);
}

/* Enhanced Responsive Design */
@media (max-width: 768px) {
    .character-icon {
        font-size: 3rem;
    }

    .content-area {
        padding: 1rem;
    }

    .game-board {
        max-width: 280px;
    }

    .game-cell {
        font-size: 1rem;
    }

    .section-card {
        margin: 0 15px;
        border-radius: 20px;
    }

    .navbar-nav {
        text-align: center;
    }

    .passcode-card {
        margin: 0 15px;
        border-radius: 20px;
    }

    .btn {
        font-size: 0.9rem;
        padding: 10px 20px;
    }
}

@media (max-width: 576px) {
    .container {
        padding: 0 10px;
    }

    .section-card {
        margin: 0 10px;
        padding: 1.5rem !important;
    }

    .character-card {
        margin: 0 10px;
    }

    .btn {
        font-size: 0.85rem;
        padding: 8px 16px;
    }

    h1, h2, h3 {
        font-size: 1.5rem;
    }

    .display-4 {
        font-size: 2rem;
    }
}

@media (min-width: 1200px) {
    .section-card {
        max-width: 900px;
        margin: 0 auto;
    }
}

/* Improved form styling */
.form-control {
    border-radius: 15px;
    padding: 12px 16px;
    border: 2px solid #e0e0e0;
    transition: all 0.3s ease;
}

.form-control:focus {
    box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
    border-color: #007bff;
    transform: translateY(-2px);
}

/* Enhanced card styling */
.card {
    border: none;
    border-radius: 20px;
    overflow: hidden;
    transition: all 0.3s ease;
}

.card:hover {
    transform: translateY(-5px);
}

/* Loading states */
.loading {
    opacity: 0.6;
    pointer-events: none;
}

.spinner-border-sm {
    width: 1rem;
    height: 1rem;
}

/* Success/Error states */
.success-message {
    color: #28a745;
}

.error-message {
    color: #dc3545;
}

static/css/themes.css.content:
/* Theme Variables and Styling */

/* Keychain Theme - Bunny/Matcha */
[data-theme="keychain"] {
    --primary-color: #ff9ebc;
    --secondary-color: #b8e6b8;
    --accent-color: #ffd1dc;
    --bg-color: #fff8f8;
    --text-color: #d4518c;
    --card-bg: rgba(255, 245, 248, 0.95);
    --navbar-bg: rgba(255, 182, 193, 0.98);
    --shadow-color: rgba(255, 158, 188, 0.3);
}

[data-theme="keychain"] body {
    background: radial-gradient(circle at 20% 20%, #ffe4e1 0%, transparent 50%),
                radial-gradient(circle at 80% 80%, #f0fff0 0%, transparent 50%),
                radial-gradient(circle at 40% 60%, #fff8dc 0%, transparent 50%),
    linear-gradient(135deg, #fff5f5 0%, #f8fff8 50%, #fffacd 100%);
    min-height: 100vh;
    position: relative;
}

[data-theme="keychain"] .garden-navbar {
    background: var(--navbar-bg) !important;
}

[data-theme="keychain"] .navbar-brand {
    color: var(--text-color) !important;
}

[data-theme="keychain"] .nav-link {
    color: var(--text-color) !important;
}

[data-theme="keychain"] .nav-link:hover {
    color: var(--primary-color) !important;
    transform: translateY(-2px);
}

[data-theme="keychain"] .section-card {
    background: var(--card-bg);
    border: 3px solid var(--primary-color);
    border-radius: 25px;
    box-shadow: 0 10px 30px var(--shadow-color);
    backdrop-filter: blur(10px);
}

[data-theme="keychain"] .btn-primary {
    background: linear-gradient(45deg, var(--primary-color), var(--accent-color));
    border: none;
    color: white;
    border-radius: 20px;
    padding: 12px 25px;
    font-weight: 600;
    text-shadow: 0 1px 2px rgba(0,0,0,0.1);
    box-shadow: 0 5px 15px var(--shadow-color);
}

[data-theme="keychain"] .btn-primary:hover {
    background: linear-gradient(45deg, var(--secondary-color), var(--primary-color));
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 8px 25px var(--shadow-color);
}

[data-theme="keychain"] .floating-particles::before {
    content: 'üå∏';
    position: absolute;
    animation: float-particle 6s ease-in-out infinite;
}

/* Bug Theme - Koala/Forest */
[data-theme="bug"] {
    --primary-color: #4caf50;
    --secondary-color: #81c784;
    --accent-color: #ffb74d;
    --bg-color: #f1f8e9;
    --text-color: #2e7d32;
    --card-bg: rgba(240, 248, 240, 0.95);
    --navbar-bg: rgba(129, 199, 132, 0.98);
    --shadow-color: rgba(76, 175, 80, 0.3);
}

[data-theme="bug"] body {
    background: radial-gradient(circle at 30% 30%, #e8f5e8 0%, transparent 50%),
                radial-gradient(circle at 70% 70%, #fff3e0 0%, transparent 50%),
                radial-gradient(circle at 50% 20%, #f3e5f5 0%, transparent 50%),
    linear-gradient(135deg, #f1f8e9 0%, #e8f5e8 50%, #fff8e1 100%);
    min-height: 100vh;
    position: relative;
}

[data-theme="bug"] .garden-navbar {
    background: var(--navbar-bg) !important;
}

[data-theme="bug"] .navbar-brand {
    color: var(--text-color) !important;
}

[data-theme="bug"] .nav-link {
    color: var(--text-color) !important;
}

[data-theme="bug"] .nav-link:hover {
    color: var(--primary-color) !important;
    transform: translateY(-2px);
}

[data-theme="bug"] .section-card {
    background: var(--card-bg);
    border: 3px solid var(--primary-color);
    border-radius: 25px;
    box-shadow: 0 10px 30px var(--shadow-color);
    backdrop-filter: blur(10px);
}

[data-theme="bug"] .btn-primary {
    background: linear-gradient(45deg, var(--primary-color), var(--accent-color));
    border: none;
    color: white;
    border-radius: 20px;
    padding: 12px 25px;
    font-weight: 600;
    text-shadow: 0 1px 2px rgba(0,0,0,0.1);
    box-shadow: 0 5px 15px var(--shadow-color);
}

[data-theme="bug"] .btn-primary:hover {
    background: linear-gradient(45deg, var(--secondary-color), var(--primary-color));
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 8px 25px var(--shadow-color);
}

[data-theme="bug"] .floating-particles::before {
    content: 'üçÉ';
    position: absolute;
    animation: float-particle 5s ease-in-out infinite;
}

/* Firefly animation for bug theme */
[data-theme="bug"] .firefly {
    position: fixed;
    width: 4px;
    height: 4px;
    background: #daa520;
    border-radius: 50%;
    box-shadow: 0 0 10px #daa520;
    animation: firefly-dance 8s ease-in-out infinite;
    z-index: 1;
}

@keyframes firefly-dance {
    0%, 100% {
        transform: translate(0, 0);
        opacity: 0.8;
    }
    25% {
        transform: translate(100px, -50px);
        opacity: 1;
    }
    50% {
        transform: translate(-50px, -100px);
        opacity: 0.6;
    }
    75% {
        transform: translate(-100px, 50px);
        opacity: 1;
    }
}

/* Dark Theme - Magical Forest */
[data-theme="dark"] {
    --primary-color: #ba68c8;
    --secondary-color: #9c27b0;
    --accent-color: #e91e63;
    --bg-color: #1a1a2e;
    --text-color: #e6e6fa;
    --card-bg: rgba(26, 26, 46, 0.95);
    --navbar-bg: rgba(186, 104, 200, 0.98);
    --shadow-color: rgba(186, 104, 200, 0.4);
}

[data-theme="dark"] body {
    background: radial-gradient(circle at 20% 20%, #2d1b69 0%, transparent 50%),
                radial-gradient(circle at 80% 80%, #4a148c 0%, transparent 50%),
                radial-gradient(circle at 40% 60%, #ad1457 0%, transparent 50%),
    linear-gradient(135deg, #0f0f23 0%, #1a1a2e 50%, #16213e 100%);
    min-height: 100vh;
    position: relative;
    color: var(--text-color);
}

[data-theme="dark"] .garden-navbar {
    background: var(--navbar-bg) !important;
}

[data-theme="dark"] .navbar-brand {
    color: var(--text-color) !important;
}

[data-theme="dark"] .nav-link {
    color: var(--text-color) !important;
}

[data-theme="dark"] .nav-link:hover {
    color: var(--accent-color) !important;
    transform: translateY(-2px);
    text-shadow: 0 0 10px var(--accent-color);
}

[data-theme="dark"] .section-card {
    background: var(--card-bg);
    border: 3px solid var(--primary-color);
    border-radius: 25px;
    box-shadow: 0 10px 30px var(--shadow-color), 0 0 20px rgba(186, 104, 200, 0.2);
    backdrop-filter: blur(10px);
    color: var(--text-color);
}

[data-theme="dark"] .btn-primary {
    background: linear-gradient(45deg, var(--primary-color), var(--accent-color));
    border: none;
    color: white;
    border-radius: 20px;
    padding: 12px 25px;
    font-weight: 600;
    text-shadow: 0 1px 2px rgba(0,0,0,0.3);
    box-shadow: 0 5px 15px var(--shadow-color);
}

[data-theme="dark"] .btn-primary:hover {
    background: linear-gradient(45deg, var(--accent-color), var(--secondary-color));
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 8px 25px var(--shadow-color), 0 0 20px var(--accent-color);
}

[data-theme="dark"] .floating-particles::before {
    content: '‚ú®';
    position: absolute;
    animation: float-particle 4s ease-in-out infinite;
}

/* Magical stars for dark theme */
[data-theme="dark"] .magic-star {
    position: fixed;
    color: var(--accent-color);
    font-size: 1rem;
    animation: twinkle 3s ease-in-out infinite;
    z-index: 1;
}

@keyframes twinkle {
    0%, 100% {
        opacity: 0.3;
        transform: scale(0.8);
    }
    50% {
        opacity: 1;
        transform: scale(1.2);
    }
}

/* Particle animations */
@keyframes float-particle {
    0%, 100% {
        transform: translateY(0px) rotate(0deg);
        opacity: 0.7;
    }
    50% {
        transform: translateY(-20px) rotate(180deg);
        opacity: 1;
    }
}

/* Theme transition effects */
body {
    transition: all 0.5s ease;
}

.theme-transition {
    transition: all 0.3s ease;
}

/* Form styling per theme */
[data-theme="keychain"] .form-control {
    border-color: var(--primary-color);
    background: rgba(255, 255, 255, 0.9);
}

[data-theme="keychain"] .form-control:focus {
    border-color: var(--secondary-color);
    box-shadow: 0 0 10px rgba(255, 182, 193, 0.5);
}

[data-theme="bug"] .form-control {
    border-color: var(--primary-color);
    background: rgba(255, 255, 255, 0.9);
}

[data-theme="bug"] .form-control:focus {
    border-color: var(--secondary-color);
    box-shadow: 0 0 10px rgba(34, 139, 34, 0.5);
}

[data-theme="dark"] .form-control {
    border-color: var(--primary-color);
    background: rgba(25, 25, 112, 0.6);
    color: var(--text-color);
}

[data-theme="dark"] .form-control:focus {
    border-color: var(--accent-color);
    box-shadow: 0 0 10px rgba(255, 105, 180, 0.5);
    background: rgba(25, 25, 112, 0.8);
}

[data-theme="dark"] .form-control::placeholder {
    color: rgba(230, 230, 250, 0.7);
}

/* Game styling per theme */
[data-theme="keychain"] .game-cell {
    border-color: var(--primary-color);
    background: rgba(255, 255, 255, 0.9);
}

[data-theme="keychain"] .game-cell:hover {
    background: var(--secondary-color);
}

[data-theme="bug"] .game-cell {
    border-color: var(--primary-color);
    background: rgba(255, 255, 255, 0.9);
}

[data-theme="bug"] .game-cell:hover {
    background: var(--secondary-color);
}

[data-theme="dark"] .game-cell {
    border-color: var(--primary-color);
    background: var(--card-bg);
    color: var(--text-color);
}

[data-theme="dark"] .game-cell:hover {
    background: var(--primary-color);
}

/* Heart game styling */
[data-theme="keychain"] .heart-game-area {
    background: linear-gradient(45deg, #fff5f5, #f0fff0);
    border-color: var(--primary-color);
}

[data-theme="bug"] .heart-game-area {
    background: linear-gradient(45deg, #f0f8f0, #e6f3e6);
    border-color: var(--primary-color);
}

[data-theme="dark"] .heart-game-area {
    background: linear-gradient(45deg, var(--card-bg), rgba(72, 61, 139, 0.6));
    border-color: var(--primary-color);
}

/* Modal and dropdown theming */
[data-theme="dark"] .dropdown-menu {
    background: var(--card-bg);
    border-color: var(--primary-color);
}

[data-theme="dark"] .dropdown-item {
    color: var(--text-color);
}

[data-theme="dark"] .dropdown-item:hover {
    background: var(--primary-color);
    color: white;
}

[data-theme="dark"] .toast {
    background: var(--card-bg);
    color: var(--text-color);
    border-color: var(--primary-color);
}

/* Responsive theme adjustments */
@media (max-width: 768px) {
    [data-theme] .floating-particles::before {
        font-size: 1.5rem;
    }

    [data-theme="dark"] .magic-star {
        font-size: 0.8rem;
    }
}

static/js/animations.js.content:
class AnimationSystem {
    constructor() {
        this.isFlowerIntroComplete = false;
        this.theme = 'default';
        this.particles = [];
        this.init();
    }

    init() {
        this.startFlowerIntro();
    }

    startFlowerIntro() {
        // Start the flower animation sequence
        console.log('Starting flower intro animation...');

        // Set a timer to complete the intro after animation
        setTimeout(() => {
            this.completeFlowerIntro();
        }, 6000); // 6 seconds for full flower animation
    }

    completeFlowerIntro() {
        console.log('Completing flower intro...');

        const flowerIntro = document.getElementById('flower-intro');
        const mainApp = document.getElementById('main-app');
        const body = document.body;

        if (flowerIntro && mainApp) {
            // Fade out flower intro
            flowerIntro.style.transition = 'opacity 1s ease-out';
            flowerIntro.style.opacity = '0';

            setTimeout(() => {
                // Hide flower intro and show main app
                flowerIntro.style.display = 'none';
                body.classList.remove('flower-animation-body');
                mainApp.classList.remove('d-none');

                // Add fade in effect to main app
                mainApp.style.opacity = '0';
                mainApp.style.transition = 'opacity 1s ease-in';

                setTimeout(() => {
                    mainApp.style.opacity = '1';
                }, 100);

                this.isFlowerIntroComplete = true;

                // Start theme particles
                this.startThemeParticles();

            }, 1000);
        }
    }

    setTheme(theme) {
        this.theme = theme;
        document.body.setAttribute('data-theme', theme);

        // Clear existing particles
        this.clearParticles();

        // Start new theme particles
        if (this.isFlowerIntroComplete) {
            this.startThemeParticles();
        }

        // Add theme transition effect
        document.body.classList.add('theme-transition');
        setTimeout(() => {
            document.body.classList.remove('theme-transition');
        }, 500);
    }

    startThemeParticles() {
        // Clear existing particles first
        this.clearParticles();

        switch(this.theme) {
            case 'keychain':
                this.createFloatingFlowers();
                break;
            case 'bug':
                this.createFireflies();
                break;
            case 'dark':
                this.createMagicStars();
                break;
        }
    }

    createFloatingFlowers() {
        const flowers = ['üå∏', 'üå∫', 'üåº', 'üåª', 'üå∑'];

        for (let i = 0; i < 6; i++) {
            setTimeout(() => {
                const flower = document.createElement('div');
                flower.textContent = flowers[Math.floor(Math.random() * flowers.length)];
                flower.className = 'floating-particles';
                flower.style.cssText = `
                    position: fixed;
                    font-size: 2rem;
                    pointer-events: none;
                    z-index: 1;
                    opacity: 0.7;
                    left: ${Math.random() * 100}vw;
                    top: ${Math.random() * 100}vh;
                    animation: float-particle 8s ease-in-out infinite;
                    animation-delay: ${Math.random() * 2}s;
                `;

                document.body.appendChild(flower);
                this.particles.push(flower);

                // Remove after animation cycle
                setTimeout(() => {
                    if (flower.parentNode) {
                        flower.parentNode.removeChild(flower);
                    }
                }, 8000);
            }, i * 500);
        }
    }

    createFireflies() {
        for (let i = 0; i < 8; i++) {
            setTimeout(() => {
                const firefly = document.createElement('div');
                firefly.className = 'firefly';
                firefly.style.cssText = `
                    left: ${Math.random() * 100}vw;
                    top: ${Math.random() * 100}vh;
                    animation-delay: ${Math.random() * 4}s;
                `;

                document.body.appendChild(firefly);
                this.particles.push(firefly);

                // Remove after animation cycle
                setTimeout(() => {
                    if (firefly.parentNode) {
                        firefly.parentNode.removeChild(firefly);
                    }
                }, 8000);
            }, i * 300);
        }
    }

    createMagicStars() {
        const stars = ['‚ú®', '‚≠ê', 'üåü', 'üí´', 'üîÆ'];

        for (let i = 0; i < 10; i++) {
            setTimeout(() => {
                const star = document.createElement('div');
                star.textContent = stars[Math.floor(Math.random() * stars.length)];
                star.className = 'magic-star';
                star.style.cssText = `
                    left: ${Math.random() * 100}vw;
                    top: ${Math.random() * 100}vh;
                    animation-delay: ${Math.random() * 3}s;
                    pointer-events: none;
                `;

                document.body.appendChild(star);
                this.particles.push(star);

                // Remove after animation cycle
                setTimeout(() => {
                    if (star.parentNode) {
                        star.parentNode.removeChild(star);
                    }
                }, 6000);
            }, i * 200);
        }
    }

    clearParticles() {
        this.particles.forEach(particle => {
            if (particle.parentNode) {
                particle.parentNode.removeChild(particle);
            }
        });
        this.particles = [];

        // Clear any remaining particles
        const existingParticles = document.querySelectorAll('.floating-particles, .firefly, .magic-star');
        existingParticles.forEach(particle => {
            if (particle.parentNode) {
                particle.parentNode.removeChild(particle);
            }
        });
    }

    // Heart collection animation
    animateHeartCollection(heartElement) {
        heartElement.classList.add('collected-heart');

        setTimeout(() => {
            if (heartElement.parentNode) {
                heartElement.parentNode.removeChild(heartElement);
            }
        }, 500);
    }

    // Button hover effects
    addButtonHoverEffects() {
        const buttons = document.querySelectorAll('.btn');

        buttons.forEach(button => {
            button.addEventListener('mouseenter', () => {
                button.style.transform = 'translateY(-2px)';
            });

            button.addEventListener('mouseleave', () => {
                button.style.transform = 'translateY(0)';
            });
        });
    }

    // Card hover effects
    addCardHoverEffects() {
        const cards = document.querySelectorAll('.section-card, .character-card');

        cards.forEach(card => {
            card.addEventListener('mouseenter', () => {
                card.style.transform = 'translateY(-5px)';
            });

            card.addEventListener('mouseleave', () => {
                card.style.transform = 'translateY(0)';
            });
        });
    }

    // Fade in animation for content
    fadeInContent(element) {
        element.style.opacity = '0';
        element.style.transform = 'translateY(20px)';
        element.style.transition = 'opacity 0.5s ease, transform 0.5s ease';

        setTimeout(() => {
            element.style.opacity = '1';
            element.style.transform = 'translateY(0)';
        }, 100);
    }

    // Refresh particle animations
    refreshParticles() {
        if (this.isFlowerIntroComplete) {
            this.startThemeParticles();
        }
    }
}

// Initialize animation system
const animationSystem = new AnimationSystem();

// Export for global use
window.animationSystem = animationSystem;

